class DraftListView(LoginRequiredMixin, ListView):
  login_url = '/login/'
  template_name = 'blog/post_draft_list.html'
  model = Post

  def get_queryset(self):
    return Post.objects.filter(published_date__isnull=True).order_by('created_date')

